package com.CoreJava;

public class Ch02_OPERATORS_03_COMPARISON_OPERATORS {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		// COMPARISON OPERATORS :- Comparison Operators are used to compare Two or More Variables.
		// They Always gives Result as Boolean Values as Result.
		
		// 1. == (EQUAL) ;- Check whether Values of Two Variables are equal or not.
		
		int a = 10;
		int b = 20;
		boolean compare = a==b;
		System.out.println(compare); // Output is 'false' because 'a is not equal to b'.
		
		
		
		// 2. != (NOT EQUAL) :- GIVES Boolean Result as "True" if two variables are 'Not Equal'.
		
		boolean compare2 = a!=b;
		System.out.println(compare2);
		
		
		/* 3. < (LESS THAN)
		*  4. > (GREATER THAN)
		*  5. <= (LESS THAN EQUAL TO)
		*  6. >= (GREATER THAN EQUAL TO)
        */
	}

}
