package com.CoreJava;

public class Data_Types_and_Variables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub\
		
		// Data Types & Variables
		
		int x = 10; // Here int is a Data Type, x is name of the Variable and 10 is Value.
		 
		// 8 Types of Primitive Data Types
		
		byte a = 34; // Can only store WHOLE Numbers between -128 to 127
		System.out.println(a); // SIZE = 1 byte
		
		short b = 3627; // Can only stores WHOLE Numbers between -32768 to 32767
		System.out.println(b); // SIZE = 2 bytes
		
		int c = 363427; // Can only store numbers between -2,147,483,648 to 2,147,483,647
		System.out.println(c); // SIZE = 4 bytes
		
		long d = 423463149139682l; // Can only Stores WHOLE numbers from -9,223,372,036,854,775,808 to 9,223,372,036,854,775,807
		System.out.println(d); // SIZE = 8 bytes
		
		char myChar = 'A'; // Stores a single character/letter or ASCII values
		System.out.println(myChar); // SIZE = 2 bytes
		
		float f = 4.90f; // Stores fractional numbers. Sufficient for storing 6 to 7 decimal digits
		System.out.println(f); // SIZE = 4 bytes
		
		double d1 = 5.29833d; // Stores fractional numbers. Sufficient for storing 15 decimal digits
		System.out.println(d1); // SIZE = 8 bytes
		
		boolean g = true; // Stores TRUE or FALSE values
		System.out.println(g); // SIZE = 1 bit
		
		
		
		
		// 1 byte == 8 bits
		
	}

}
