package com.CoreJava;

public class Literals {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// LITERALS
		
		// A Constant value which can be assigned to the variable is called as a 'Literal'.
		
		byte a = 34; // integer Literal
		short b = 3627; // integer Literal
		int c = 363427; // integer Literal
		
		long d = 423463149139682l; // long Literal
		
		char myChar = 'A'; // Character Literal
		
		float f = 4.90f; // float Literal (decimal Literal)
		
		double d1 = 5.29833; // double Literal (Default type for decimals)
		
		boolean g = true; // boolean Literal
		
		String str = "Rahul"; // String Literal

	}

}
